    public static int[] addNegabinary(int[] arr1, int[] arr2) {
        List<Integer> result = new ArrayList<>();
        /**
         * 数组arr1表示的负二进制数的十进制值
         */
        BigInteger value1 = BigInteger.ZERO;
        /**
         * 数组arr2表示的负二进制数的十进制值
         */
        BigInteger value2 = BigInteger.ZERO;
        /**
         * 数组arr1中每一个数字代表的权重
         */
        BigInteger weight1 = BigInteger.ONE;
        /**
         * 数组arr2中每一个数字代表的权重
         */
        BigInteger weight2 = BigInteger.ONE;
        BigInteger scale = BigInteger.valueOf(-2);
        /**
         * 计算数组arr1表示的负二进制数的十进制值
         */
        for (int i = arr1.length - 1; i >= 0; i--) {
            value1 = value1.add(BigInteger.valueOf(arr1[i]).multiply(weight1));
            weight1 = weight1.multiply(scale);
        }
        /**
         * 计算数组arr2表示的负二进制数的十进制值
         */
        for (int i = arr2.length - 1; i >= 0; i--) {
            value2 = value2.add(BigInteger.valueOf(arr2[i]).multiply(weight2));
            weight2 = weight2.multiply(scale);
        }
        BigInteger sum = value1.add(value2);
        /**
         * 通过短除法将十进制数sum转换为对应的负二进制数
         */
        while (sum.intValue() != 0) {
            /**
             * 对sum/-2取余
             */
            int remainder = sum.remainder(scale).intValue();
            sum = sum.divide(scale);

            if (remainder == -1) {
                /**
                 * 将a/-2=x...-1转换成a/-2=(x+1)...1的形式，因为-2(x+1)+1=-2x-1，即商增加1，余数修改为1
                 */
                result.add(1);
                /**
                 * 商增加1
                 */
                sum = sum.add(BigInteger.ONE);
            } else {
                result.add(remainder);
            }
        }
        Collections.reverse(result);
        return result.isEmpty() ? new int[]{0} : result.stream().mapToInt(x -> x).toArray();
    }
