    /**
     * 看不懂！！！
     * 参考：<a href="https://leetcode.cn/problems/maximum-product-of-two-integers-with-no-common-bits/solutions/3768219/mo-ban-gao-wei-qian-zhui-he-sos-dppython-78fz/"></a>
     *
     * @param nums
     * @return
     */
    public static long maxProduct(int[] nums) {
        long result = 0L;
        int max = Arrays.stream(nums).max().getAsInt();
        /**
         * max二进制值的长度
         */
        int bitLength = 32 - Integer.numberOfLeadingZeros(max);
        int upperLimit = 1 << bitLength;
        int[] dp = new int[upperLimit];

        for (int num : nums) {
            dp[num] = num;
        }
        /**
         * 从小到大枚举集合i
         */
        for (int i = 0; i < bitLength; i++) {
            /**
             * 枚举集合i中的元素j
             */
            for (int j = 0; j < upperLimit; j++) {
                /**
                 * 判断元素j时候属于集合i
                 */
                if ((j >> i & 1) > 0) {
                    /**
                     * 从集合i不包含元素j的子集转移过来
                     */
                    dp[j] = Math.max(dp[j], dp[j ^ (1 << i)]);
                }
            }
        }

        for (int num : nums) {
            result = Math.max(result, (long) num * dp[(upperLimit - 1) ^ num]);
        }
        return result;
    }
