package leetcode.algorithms;

/**
 * Description: 3234. Count the Number of Substrings With Dominant Ones
 *
 * @author Baltan
 * @date 2024/7/28 19:20
 */
public class NumberOfSubstrings2 {
    public static void main(String[] args) {
        System.out.println(numberOfSubstrings("00011"));
        System.out.println(numberOfSubstrings("101101"));
        System.out.println(numberOfSubstrings(""));
    }

    /**
     * 参考：<a href="https://leetcode.cn/problems/count-the-number-of-substrings-with-dominant-ones/solutions/2860181/mei-ju-by-tsreaper-x830/"></a>
     *
     * @param s
     * @return
     */
    public static int numberOfSubstrings(String s) {
        int result = 0;
        int length = s.length();
        /**
         * nextZeroIndexes[i]表示字符串s中，s[i]后的第一个0的索引值（包括s[i]自身）
         */
        int[] nextZeroIndexes = new int[length + 2];
        /**
         * 假设字符串s后面还有两个0，方便计算
         */
        nextZeroIndexes[length + 1] = length + 1;
        nextZeroIndexes[length] = length;

        for (int i = length - 1; i >= 0; i--) {
            /**
             * 如果s[i]本身就是0，则s[i]后的第一个0的索引值就是i，否则和s[i+1]后的第一个0的索引值相同
             */
            nextZeroIndexes[i] = s.charAt(i) == '0' ? i : nextZeroIndexes[i + 1];
        }
        /**
         * 枚举所有以字符s[i]开头的子串
         */
        for (int i = 0; i < length; i++) {
            /**
             * 不包含0的子串个数都是"1显著的字符串"
             */
            result += nextZeroIndexes[i] - i;
            /**
             * 假设j为s[i]后的第一个0的索引值，k为s[i]后的第二个0的索引值。子串s.substring(i,k)中0的个数为zeroCount个，则1的个数为
             * k-i-zeroCount个，如果1的个数不少于0的个数，即k-i-zeroCount-zeroCount*zeroCount不小于0，则子串s.substring(i,k)是一
             * 个"1显著的字符串"，此时可以从s[k-1]开始向前逐一删除字符1，至多删除more个1，同时不能超过s[j]，即向前可以删除1的个数为
             * [0,Math.min(more,k-j-1)]，操作后得到的子串s.substring(i,?)都是"1显著的字符串"，共Math.min(more,k-j-1)+1个子串。
             * 另外，因为zeroCount^2<=oneCount<=length，所以zeroCount*zeroCount<length
             */
            for (int j = nextZeroIndexes[i], k = nextZeroIndexes[j + 1], zeroCount = 1; zeroCount * zeroCount <= length && k + 1 < nextZeroIndexes.length; ) {
                int more = k - i - zeroCount - zeroCount * zeroCount;

                if (more >= 0) {
                    result += Math.min(more, k - j - 1) + 1;
                }
                /**
                 * 继续判断以字符s[i]开头，并且包括s[k]的子串
                 */
                zeroCount++;
                j = k;
                k = nextZeroIndexes[k + 1];
            }
        }
        return result;
    }
}
